// Rust
use std::collections::HashMap;

struct Bank {
    balances： HashMap<String， u32>,
}

impl Bank {
    pub fn new() -> Bank {
        Bank { balances： HashMap::new() }
    }

    pub fn deposit(&mut self， addr： &str， amount： u32) {
        *self.balances.entry(addr.to_string()).or_insert(0) += amount;
    }

    pub fn withdraw(&mut self， addr： &str， amount： u32) -> Result<()， String> {
        let entry = self.balances.get_mut(addr);
        match entry {
            Some(v) if *v >= amount => {
                *v -= amount;
                Ok(())
            },
            _ => Err("Insufficient funds".to_string()),
        }
    }
}

