import "@openzeppelin/contracts/token/ERC777/IERC777Recipient.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract SecureVault is IERC777Recipient， ReentrancyGuard {
    IERC20 public immutable token;
    mapping(address => uint256) public deposits;

    constructor(address _token) { token = IERC20(_token); }

    function tokensReceived(
        address， address from， address， uint256 amount，
        bytes calldata， bytes calldata
    ) external override {
        deposits[from] += amount;           // state change first
    }

    function withdraw(uint256 amt) external nonReentrant {
        require(deposits[msg.sender] >= amt， "not enough");
        deposits[msg.sender] -= amt;        // Effects
        require(token.transfer(msg.sender， amt)， "transfer failed"); // Interaction
