// A contract that can be used as a library for the main contract.
contract LibraryContract {
    uint256 public value;

    function setValue(uint256 _value) public {
        value = _value;
    }
}

// The main contract that uses delegatecall to call the library.
// This contract is vulnerable because the library address can be changed by anyone.
contract VulnerableProxy {
    address public implementation;
    address public owner;

    constructor(address _implementation) {
        implementation = _implementation;
        owner = msg.sender;
    }

    // The attacker can change the implementation address to a malicious contract.
    function setImplementation(address _newImplementation) public {
        implementation = _newImplementation;
    }

    // The fallback function that delegates calls to the implementation contract.
    fallback() external payable {
        (bool success， bytes memory data) = implementation.delegatecall(msg.data);
        require(success， "Delegatecall failed");
    }
}

