struct Escrow {
    funds: std::collections::HashMap<String， u64>，
    pending_releases: Vec<String>，
}

impl Escrow {
    fn deposit(&mut self， account: String， amount: u64) {
        let balance = self.funds.entry(account.clone()).or_insert(0);
        *balance += amount;
        self.pending_releases.push(account);
    }

    fn release_funds(&mut self， account: &str) -> Result<u64， &'static str> {
        let funds = self.funds.get(account).ok_or("No funds found")?;
        let amount = *funds;
        self.funds.remove(account);
        self.pending_releases.retain(|acc| acc != account);
        Ok(amount)
    }
}

