use std::collections::HashMap;
use std::rc::Rc;

trait EventObserver {
    fn on_event(&self， event: &str， data: &HashMap<String， String>);
}

struct EventData {
    event_type: String，
    data: HashMap<String， String>，
}

struct ContractEventSystem {
    observers: HashMap<String， Vec<Rc<dyn EventObserver>>>，
    event_history: Vec<Rc<EventData>>，
    max_history: usize，
}

impl ContractEventSystem {
    fn new(max_history: usize) -> Self {
        ContractEventSystem {
            observers: HashMap::new()，
            event_history: Vec::new()，
            max_history，
        }
    }

    fn subscribe(&mut self， event_type: String， observer: Rc<dyn EventObserver>) {
        self.observers.entry(event_type).or_insert_with(Vec::new).push(observer);
    }

    fn emit_event(&mut self， event_type: String， data: HashMap<String， String>) {
        let event_data = Rc::new(EventData { event_type: event_type.clone()， data });

        // Bounded history to prevent memory leaks
        if self.event_history.len() >= self.max_history {
            self.event_history.remove(0);
        }
        self.event_history.push(event_data.clone());

        // Efficient notification using references
        if let Some(observers) = self.observers.get(&event_type) {
            for observer in observers {
                observer.on_event(&event_data.event_type， &event_data.data);
            }
        }
    }
}

