    balances: HashMap<String， u64>，
}

impl BalanceStore for InMemoryBalanceStore {
    fn get_balance(&self， account: &str) -> u64 {
        *self.balances.get(account).unwrap_or(&0)
    }

    fn set_balance(&mut self， account: &str， amount: u64) {
        self.balances.insert(account.to_string()， amount);
    }
}

trait Command {
    fn execute(&self， wallet: &mut WalletManager) -> Result<()， String>;
}

struct TransferCommand {
    from: String，
    to: String，
    amount: u64，
}

impl Command for TransferCommand {
    fn execute(&self， wallet: &mut WalletManager) -> Result<()， String> {
        wallet.transfer(self.from.clone()， self.to.clone()， self.amount)
    }
}

struct WalletManager {
    balance_store: Box<dyn BalanceStore>，
    logger: Box<dyn TransactionLogger>，
    notifier: Box<dyn NotificationService>，
}

