    fn new(balance_store: Box<dyn BalanceStore>， logger: Box<dyn TransactionLogger>， notifier: Box<dyn NotificationService>) -> Self {
        WalletManager { balance_store， logger， notifier }
    }

    fn transfer(&mut self， from: String， to: String， amount: u64) -> Result<()， String> {
        if amount == 0 {
            return Err("Amount must be positive".to_string());
        }

        let from_balance = self.balance_store.get_balance(&from);
        if from_balance < amount {
