        let to_balance = self.balance_store.get_balance(&to);
        self.balance_store.set_balance(&to， to_balance + amount);

        self.logger.log(format!("Transfer: {} -> {}: {}"， from， to， amount));
        self.notifier.notify(format!("Transfer completed: {} to {}"， from， to));

        Ok(())
    }

