struct OrderPool {
    available: Vec<Order>，
    capacity: usize，
}

impl OrderPool {
    fn new(capacity: usize) -> Self {
        Self {
            available: Vec::with_capacity(capacity)，
            capacity，
        }
    }

    fn get_order(&mut self， id: u64， user_id: u64， amount: u64， price: u64) -> Order {
        if let Some(mut order) = self.available.pop() {
            order.id = id;
            order.user_id = user_id;
            order.amount = amount;
            order.price = price;
            order.is_active = true;
            order
        } else {
            Order { id， user_id， amount， price， is_active: true }
        }
    }

    fn return_order(&mut self， order: Order) {
        if self.available.len() < self.capacity {
            self.available.push(order);
        }
    }
}

struct OrderBook {
    active_orders: Vec<Order>，
    order_pool: OrderPool，
    next_id: u64，
}

impl OrderBook {
    fn new() -> Self {
        Self {
            active_orders: Vec::new()，
            order_pool: OrderPool::new(100)，
            next_id: 1，
        }
    }

    fn create_order(&mut self， user_id: u64， amount: u64， price: u64) -> u64 {
        let order = self.order_pool.get_order(self.next_id， user_id， amount， price);
        self.next_id += 1;
        self.active_orders.push(order);
        self.next_id - 1
    }

    fn cancel_order(&mut self， order_id: u64) -> bool {
        if let Some(pos) = self.active_orders.iter().position(|o| o.id == order_id) {
            let order = self.active_orders.remove(pos);
            self.order_pool.return_order(order);
            true
        } else {
            false
        }
    }
}

