use std::collections::HashMap;

struct BatchProcessor {
    data_cache: HashMap<String， Vec<u64>>，
    results: Vec<ProcessingResult>，
}

struct ProcessingResult {
    key: String，
    sum: u64，
    count: usize，
    average: f64，
}

impl BatchProcessor {
    fn new() -> Self {
        Self {
            data_cache: HashMap::new()，
            results: Vec::new()，
        }
    }

    fn process_batch(&mut self， batch_data: Vec<(String， Vec<u64>)>) -> Vec<ProcessingResult> {
        let mut results = Vec::with_capacity(batch_data.len());

        for (key， values) in batch_data {
            let sum: u64 = values.iter().sum();
            let count = values.len();
            let average = sum as f64 / count as f64;

            let result = ProcessingResult {
                key: key.clone()，
                sum，
                count，
                average，
            };

            results.push(result);
            self.data_cache.insert(key， values);
        }

        self.results.extend(results.iter().cloned());
        results
    }

    fn get_cached_data(&self， key: &str) -> Option<&Vec<u64>> {
        self.data_cache.get(key)
    }

    fn process_streaming(&mut self， data_stream: impl Iterator<Item = (String， Vec<u64>)>) -> impl Iterator<Item = ProcessingResult> + '_ {
        data_stream.map(|(key， values)| {
            let sum: u64 = values.iter().sum();
            let count = values.len();
            let average = sum as f64 / count as f64;

            let result = ProcessingResult { key: key.clone()， sum， count， average };
            self.data_cache.insert(key， values);
            result
        })
    }

    fn clear_cache(&mut self) {
        self.data_cache.clear();
        self.results.clear();
    }
}

