            voters: Rc::new(RefCell::new(HashMap::new()))，
            next_id: Rc::new(RefCell::new(1))，
        }
    }

    pub fn create_proposal(&self， title: String) -> u32 {
        let id = *self.next_id.borrow();
        *self.next_id.borrow_mut() += 1;

        let proposal = Proposal {
            id，
            title，
            votes_for: 0，
            votes_against: 0，
            active: true，
        };

        self.proposals.borrow_mut().insert(id， proposal);
        id
    }

    pub fn vote(&self， voter: &str， proposal_id: u32， vote_for: bool) -> Result<()， String> {
