        let user_votes = voted_proposals.entry(voter.to_string()).or_insert(Vec::new());

        if user_votes.contains(&proposal_id) {
            return Err("Already voted".to_string());
        }

        user_votes.push(proposal_id);

        if let Some(proposal) = self.proposals.borrow_mut().get_mut(&proposal_id) {
            if vote_for {
                proposal.votes_for += 1;
            } else {
                proposal.votes_against += 1;
            }
            Ok(())
        } else {
            Err("Proposal not found".to_string())
        }
    }
}

