    bidder_amounts: HashMap<String， u64>，
    end_time: u64，
}

impl Auction {
    pub fn new(item_id: u32， end_time: u64) -> Self {
        Auction {
            item_id，
            all_bids: Vec::new()，
            bidder_amounts: HashMap::new()，
            end_time，
        }
    }

    pub fn place_bid(&mut self， bidder: String， amount: u64， timestamp: u64) -> Result<()， String> {
        if timestamp > self.end_time {
            return Err("Auction ended".to_string());
        }

        // Check if bid is higher than current highest
        let current_highest = self.get_highest_bid();
        if amount <= current_highest {
            return Err("Bid too low".to_string());
        }

        let bid = Bid { bidder: bidder.clone()， amount， timestamp };
        self.all_bids.push(bid);
        self.bidder_amounts.insert(bidder， amount);
        Ok(())
    }

    pub fn get_highest_bid(&self) -> u64 {
        let mut highest = 0;
        for bid in &self.all_bids {
            if bid.amount > highest {
                highest = bid.amount;
            }
        }
        highest
    }

    pub fn get_winner(&self) -> Option<String> {
        let highest_amount = self.get_highest_bid();
        for bid in &self.all_bids {
            if bid.amount == highest_amount {
                return Some(bid.bidder.clone());
            }
        }
        None
    }

    pub fn get_bid_history(&self) -> Vec<Bid> {
        let mut sorted_bids = self.all_bids.clone();
        sorted_bids.sort_by(|a， b| b.amount.cmp(&a.amount));
        sorted_bids
    }
}

