use std::collections::HashMap;

trait CallbackHandler {
    fn handle_callback(&mut self， caller: String， data: Vec<u8>) -> Result<()， String>;
}

struct CallbackRegistry {
    callbacks: HashMap<String， Box<dyn CallbackHandler>>，
    pending_calls: HashMap<String， Vec<Vec<u8>>>，
    max_pending_per_contract: usize，
}

impl CallbackRegistry {
    fn new(max_pending: usize) -> Self {
        Self {
            callbacks: HashMap::new()，
            pending_calls: HashMap::new()，
            max_pending_per_contract: max_pending，
        }
    }

    fn register_callback(&mut self， contract_id: String， handler: Box<dyn CallbackHandler>) -> Result<()， String> {
        self.callbacks.insert(contract_id.clone()， handler);

        if let Some(pending) = self.pending_calls.remove(&contract_id) {
            for data in pending {
                if let Some(handler) = self.callbacks.get_mut(&contract_id) {
                    handler.handle_callback(contract_id.clone()， data)?;
                }
            }
        }
        Ok(())
    }

    fn execute_callback(&mut self， contract_id: String， data: Vec<u8>) -> Result<()， String> {
        if let Some(handler) = self.callbacks.get_mut(&contract_id) {
            handler.handle_callback(contract_id.clone()， data)
        } else {
            let pending = self.pending_calls.entry(contract_id.clone()).or_insert_with(Vec::new);
            if pending.len() >= self.max_pending_per_contract {
                return Err("Too many pending callbacks for contract".to_string());
            }
            pending.push(data);
            Ok(())
        }
    }
}

