pub struct GovernanceContract {
    owner: String，
    proposals: Vec<Proposal>，
    voting_power: std::collections::HashMap<String， u32>，
    voted: std::collections::HashMap<(String， u32)， bool>，
}

impl GovernanceContract {
    pub fn execute_proposal(&mut self， proposal_id: u32， executor: &str) -> Result<()， String> {
        if executor != self.owner {
            return Err("Unauthorized executor".to_string());
        }

        if let Some(proposal) = self.proposals.get_mut(proposal_id as usize) {
            if proposal.executed {
                return Err("Proposal already executed".to_string());
            }
            if proposal.votes_for <= proposal.votes_against {
                return Err("Insufficient votes".to_string());
            }
            proposal.executed = true;
            Ok(())
        } else {
            Err("Proposal not found".to_string())
        }
    }

    pub fn vote(&mut self， voter: &str， proposal_id: u32， support: bool) -> Result<()， String> {
        let vote_key = (voter.to_string()， proposal_id);
        if self.voted.contains_key(&vote_key) {
            return Err("Already voted".to_string());
        }

        let power = *self.voting_power.get(voter).unwrap_or(&1);
        if let Some(proposal) = self.proposals.get_mut(proposal_id as usize) {
            if proposal.executed {
                return Err("Proposal already executed".to_string());
            }
            self.voted.insert(vote_key， true);
            if support {
                proposal.votes_for += power;
            } else {
                proposal.votes_against += power;
            }
            Ok(())
        } else {
            Err("Proposal not found".to_string())
        }
    }
}

