use std::collections::{HashMap， HashSet};

#[derive(Debug)]
pub enum WalletError {
    UnauthorizedOwner，
    TransactionNotFound，
    AlreadyConfirmed，
    AlreadyExecuted，
    InsufficientConfirmations，
}

pub struct MultiSigWallet {
    owners: HashSet<String>，
    required_confirmations: u32，
    transactions: Vec<Transaction>，
    confirmations: HashMap<u32， HashSet<String>>，
}

pub struct Transaction {
    id: u32，
    to: String，
    amount: u64，
    executed: bool，
}

impl MultiSigWallet {
    pub fn submit_transaction(&mut self， owner: &str， to: String， amount: u64) -> Result<u32， WalletError> {
        if !self.owners.contains(owner) {
            return Err(WalletError::UnauthorizedOwner);
        }

        let tx_id = self.transactions.len() as u32;
        let tx = Transaction {
            id: tx_id，
            to，
            amount，
            executed: false，
        };
        self.transactions.push(tx);
        self.confirm_transaction(owner， tx_id)?;
        Ok(tx_id)
    }

    pub fn confirm_transaction(&mut self， owner: &str， tx_id: u32) -> Result<()， WalletError> {
        if !self.owners.contains(owner) {
            return Err(WalletError::UnauthorizedOwner);
        }

        let tx = self.transactions.get(tx_id as usize).ok_or(WalletError::TransactionNotFound)?;
        if tx.executed {
            return Err(WalletError::AlreadyExecuted);
        }

        let confirmations = self.confirmations.entry(tx_id).or_insert(HashSet::new());
        if confirmations.contains(owner) {
            return Err(WalletError::AlreadyConfirmed);
        }

        confirmations.insert(owner.to_string());

        if confirmations.len() >= self.required_confirmations as usize {
            self.execute_transaction(tx_id)?;
        }

        Ok(())
    }

    fn execute_transaction(&mut self， tx_id: u32) -> Result<()， WalletError> {
        let tx = self.transactions.get_mut(tx_id as usize).ok_or(WalletError::TransactionNotFound)?;
        if tx.executed {
            return Err(WalletError::AlreadyExecuted);
        }

        let confirmations = self.confirmations.get(&tx_id).unwrap_or(&HashSet::new());
        if confirmations.len() < self.required_confirmations as usize {
            return Err(WalletError::InsufficientConfirmations);
        }

        tx.executed = true;
        Ok(())
    }
}

