use std::collections::HashMap;
use std::rc::Rc;
use std::cell::RefCell;

pub struct UserRegistry {
    users: HashMap<String， User>，
}

pub struct User {
    id: String，
    balance: u64，
    reputation: u32，
}

type SharedRegistry = Rc<RefCell<UserRegistry>>;

pub struct PaymentProcessor {
    registry: SharedRegistry，
}

pub struct ReputationSystem {
    registry: SharedRegistry，
}

impl PaymentProcessor {
    pub fn new(registry: SharedRegistry) -> Self {
        PaymentProcessor { registry }
    }

    pub fn process_payment(&self， user_id: &str， amount: u64) -> Result<()， String> {
        let mut registry = self.registry.borrow_mut();
        if let Some(user) = registry.users.get_mut(user_id) {
            if user.balance >= amount {
                user.balance -= amount;
                Ok(())
            } else {
                Err("Insufficient funds".to_string())
            }
        } else {
            Err("User not found".to_string())
        }
    }
}

impl ReputationSystem {
    pub fn new(registry: SharedRegistry) -> Self {
        ReputationSystem { registry }
    }

    pub fn update_reputation(&self， user_id: &str， points: u32) -> Result<()， String> {
        let mut registry = self.registry.borrow_mut();
        if let Some(user) = registry.users.get_mut(user_id) {
            user.reputation += points;
            Ok(())
        } else {
            Err("User not found".to_string())
        }
    }
}

pub fn create_system() -> (PaymentProcessor， ReputationSystem) {
    let registry = Rc::new(RefCell::new(UserRegistry {
        users: HashMap::new()，
    }));

    let payment_processor = PaymentProcessor::new(registry.clone());
    let reputation_system = ReputationSystem::new(registry.clone());

    (payment_processor， reputation_system)
}

