pragma solidity ^0.8.22;

// This contract is vulnerable to performance issues with a large number of stakers.
// The reward calculation is inefficient and can lead to out-of-gas errors.
contract VulnerableStaking {
    mapping(address => uint256) public stakes;
    mapping(address => uint256) public rewards;
    address[] public stakers;

    function stake() external payable {
        require(msg.value > 0， "Cannot stake 0");
        if (stakes[msg.sender] == 0) {
            stakers.push(msg.sender);
        }
        stakes[msg.sender] += msg.value;
    }

    // This function is very inefficient and will consume a lot of gas with many stakers.
    function distributeRewards() external {
        for (uint256 i = 0; i < stakers.length; i++) {
            address staker = stakers[i];
            rewards[staker] += calculateReward(staker);
        }
    }

    // A simple reward calculation function.
    function calculateReward(address staker) internal view returns (uint256) {
        return stakes[staker] / 100;
    }

    function withdraw() external {
        uint256 amount = stakes[msg.sender];
        require(amount > 0， "No stake to withdraw");
        stakes[msg.sender] = 0;
        payable(msg.sender).transfer(amount);
    }
}

