use std::sync::{Arc， Mutex};
use std::thread;

#[derive(Debug， Clone)]
struct MultiSigWallet {
    owners: Vec<String>，
    required_signatures: usize，
    pending_transactions: Arc<Mutex<Vec<Transaction>>>，
}

#[derive(Debug， Clone)]
struct Transaction {
    id: u64，
    to: String，
    amount: u64，
    signatures: Vec<String>，
    executed: bool，
}

impl MultiSigWallet {
    fn new(owners: Vec<String>， required_sigs: usize) -> Self {
        MultiSigWallet {
            owners，
            required_signatures: required_sigs，
            pending_transactions: Arc::new(Mutex::new(Vec::new()))，
        }
    }

    fn sign_transaction(&self， tx_id: u64， signer: String) -> bool {
        let mut transactions = self.pending_transactions.lock().unwrap();
        for tx in transactions.iter_mut() {
            if tx.id == tx_id && !tx.executed {
                tx.signatures.push(signer);
                if tx.signatures.len() >= self.required_signatures {
                    tx.executed = true;
                    return true;
                }
            }
        }
        false
    }
}

