    total_supply: u64，
}

impl TokenInterface for Token {
    fn transfer(&mut self， from: &str， to: &str， amount: u64) -> Result<()， String> {
        let from_balance = self.balances.get(from).unwrap_or(&0);

        if from_balance < &amount {
            return Err("Insufficient balance".to_string());
        }

        let to_balance = self.balances.get(to).unwrap_or(&0);
        self.balances.insert(from.to_string()， from_balance - amount);
        self.balances.insert(to.to_string()， to_balance + amount);
        Ok(())
    }

    fn mint(&mut self， to: &str， amount: u64) -> Result<()， String> {
        let balance = self.balances.get(to).unwrap_or(&0);

        if let Some(new_balance) = balance.checked_add(amount) {
            self.balances.insert(to.to_string()， new_balance);
            self.total_supply = self.total_supply.checked_add(amount)
                .ok_or("Total supply overflow")?;
            Ok(())
        } else {
            Err("Balance overflow".to_string())
        }
    }

    fn burn(&mut self， from: &str， amount: u64) -> Result<()， String> {
        let balance = self.balances.get(from).unwrap_or(&0);

        if balance < &amount {
            return Err("Insufficient balance to burn".to_string());
        }

        self.balances.insert(from.to_string()， balance - amount);
        self.total_supply -= amount;
        Ok(())
    }

    fn balance_of(&self， account: &str) -> u64 {
        *self.balances.get(account).unwrap_or(&0)
    }
}

