struct Vault {
    balances: HashMap<String， u64>，
    total_deposits: u64，
    locked: bool，
}

impl Vault {
    fn withdraw(&mut self， user: &str， amount: u64) -> Result<()， &'static str> {
        if self.locked {
            return Err("Reentrant call detected");
        }

        let balance = self.balances.get(user).unwrap_or(&0);
        if *balance >= amount {
            // Update state before external call
            self.balances.insert(user.to_string()， balance - amount);
            self.total_deposits -= amount;

            // Set reentrancy guard
            self.locked = true;
            let result = self.external_transfer(user， amount);
            self.locked = false;

            result
        } else {
            Err("Insufficient balance")
        }
    }

    fn external_transfer(&self， _user: &str， _amount: u64) -> Result<()， &'static str> {
        Ok(())
    }
}
